import axios from 'axios';

// Create axios instance with default configuration
const apiClient = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8000',
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
apiClient.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('accessToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
apiClient.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const API_ENDPOINTS = {
  // Property Data Collector
  PROPERTY_DATA_COLLECT: '/api/v1/property-data/collect',
  
  // Price Estimator
  PRICE_ESTIMATE: '/api/v1/price-estimator/estimate',
  
  // Location Analyzer
  LOCATION_ANALYZE: '/api/v1/location-analyzer/analyze',
  
  // Deal Evaluator
  DEAL_EVALUATE: '/api/v1/deal-evaluator/evaluate',
  
  // Full Analysis
  FULL_ANALYSIS: '/api/v1/analysis/full',
  
  // Health check
  HEALTH: '/health',
  
  // Root
  ROOT: '/',
};

// Helper functions
export const apiRequest = async (method, endpoint, data = null, config = {}) => {
  try {
    const response = await apiClient.request({
      method,
      url: endpoint,
      data,
      ...config,
    });
    return response.data;
  } catch (error) {
    console.error(`API Error (${method} ${endpoint}):`, error);
    throw error;
  }
};

// Convenience methods
export const apiGet = (endpoint, config) => apiRequest('GET', endpoint, null, config);
export const apiPost = (endpoint, data, config) => apiRequest('POST', endpoint, data, config);
export const apiPut = (endpoint, data, config) => apiRequest('PUT', endpoint, data, config);
export const apiDelete = (endpoint, config) => apiRequest('DELETE', endpoint, null, config);

export default apiClient;
